<body id="embedpage"><script src='https://www.google.com/recaptcha/api.js'></script><div id="overlay"></div><div id="container">    <div class="settings-container clearfix">        <h1>Gerenciar email</h1>        <div id="back-link">            <a href="%www%/identity/avatars">Meus avatares</a> &raquo; <a href="%www%/identity/settings"> Gerenciar                emails</a> &raquo; Trocar email        </div>        <div style="padding: 0 10px">            %errors%            <form action="%www%/identity/mail_change" id="change-password" method="post">                <input type="hidden" name="fromClient" value="false"/>                <div class="field field-currentpassword">                    <label for="current-password">Senha atual</label>                    <input type="password" id="current-password" size="35" name="currentPassword" value=""                           class="password-field" maxlength="32"/>                    <p class="help"></p>                </div>                <div class="form-box">                    <div class="field field-password">                        <label for="password">Email atual</label>                        <input type="email" readonly="readonly" id="email" disabled=disabled size="70"                               value="abv@abdas.com"                               class="password-field" maxlength="32"/>                    </div>                    <div class="field field-password2">                        <label for="password2">Novo email</label>                        <input type="email" id="mail" size="70" name="newMail" value=""                               class="password-field" maxlength="32"/>                    </div>                </div>                <div class="field field-recaptcha">                    <div id="captcha-container" class="input-field">                        <div class="g-recaptcha"                             data-sitekey="6Ld8ShAUAAAAAC_2-ccVvXhefP6AQs315Z3jb0zv"></div>                    </div>                </div>                <div class="js" style="overflow: hidden">                    <a href="#" class="new-button password-button" id="next-btn"                       onclick="$(this).up('form').submit(); return false;"><b>Enviar</b><i></i></a>                    <input type="submit" id="next" value="Verander" style="display: none;"/>                </div>            </form>        </div>    </div>    <div class="settings-container-bottom"></div>